        // if (!(regexpass.test(itempassword))) {
          //   setpassword(true)
          // } else {
            //   setpassword(false)
            // }





          const validateform = (e) => {
            e.preventDefault()
            // let email=document.getElementById('InputEmail').value

            // if(!pattern.test(email)){
              //   document.getElementById("emailerror").innerHTML=" * please enter valid email"
              // }



              // let password=document.getElementById("InputPassword").value
              // let regexpass = /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{8,}$/;
              // if(!regexpass.test(password)){
                //   document.getElementById("passerror").innerHTML="* Minimum eight characters, at least one letter and one number"
                // } 
                const  handleEmail = (event) => {
                  setemail({
                    value: event.target.value,
                    emailerror: isEmailValidation(event.target.value) ? "invalid email" : "",
                  });
                };
                const handlePassword = (event) => {
                  setpassword({
                    value: event.target.value,
                    passerror: isPasswordValidation(event.target.value,)
                      ? "password must greter than 6"
                      : "",
                  }); 
                };
    // else{
    //   document.getElementById("passerror").innerHTML=""
    // }











  return (
    <>
      <ul>
        <li>
          <Link to={"/"}>Register</Link>

        </li>
        <li>
          <Link to={"/login"}>Login</Link>

        </li>
      </ul>
      <div className='container' >
        <img src={img2} alt="" />
        <form className='container' onSubmit={validateform} name="myForm">
          <div className="mb-3 ">
            <label for="InputEmail" className="form-label">Email address</label>
            <input type="email" name='email' onChange={(event)=>handleEmail(event)} value={email.value} className="form-control" id="InputEmail" aria-describedby="emailHelp" />{rerror && <span className='emailerror'>invalid email</span>}


            <span id='emailerror'></span>
            <div id="emailHelp" className="form-text"></div>
          </div>
          <div className="mb-3">
            <label for="InputPassword" className="form-label">Password</label>
            <input type="text" name='password' className="form-control" value={password.value} id="InputPassword"  onChange={(event)=>handlePassword(event)}/>
            {password && <span className='passerror'>Minimum eight characters, at least one letter and one number</span>}
            <span id='passerror'></span>
          </div>
          {error || password ? <button type="submit" className="btn btn-primary" disabled>Submit</button> : <button type="submit" className="btn btn-primary"  >Submit</button>}
        </form>
      </div>

    </>
  )
}
