import { React, useState } from 'react'
import { Link } from 'react-router-dom'
import img1 from "../../images/software.jpg"
import {
  isEmailValidation,
  isPasswordValidation,
  isusernameValidation,

} from '../../utils/utils'


export default function RegisterBody() {
  const [email, setemail] = useState({
    value: "",
    emailerror: ""
  })

  const [password, setpassword] = useState({
    value: "",
    passerror: ""
  })

  const [username, setusername] = useState({
    value: "",
    usererror: ""
  })

  const [mobileno, setmobileno] = useState({
    value: "",
    mobilenoerror: ""
  })

  const handleEmail = (event) => {
    setemail({
      value: event.target.value,
      emailerror: isEmailValidation(event.target.value) ? "invalid email" : "",
    });
  };
  const handlePassword = (event) => {
    setpassword({
      value: event.target.value,
      passerror: isPasswordValidation(event.target.value,)
        ? "password must greater than 6"
        : "",
    });
  };

  const handleUsername = (event) => {
    setusername({
      value: event.target.value,
      passerror: isusernameValidation(event.target.value)
        ? "username must be filled"
        : ""
    })
  }






  const validateform = (e) => {

    e.preventDefault()





  }

